import React, { Component } from "react";
import "./Recovery.css";

import ArrowBack from "../../components/ArrowBack";
import { Form, Button } from "react-bootstrap";

class Recovery extends Component {
  state = {
    value: +372,
    phoneNumber: "",
    showPhoneNumber: null,
    submit: false,
  };
  change = (event) => {
    this.setState({ value: event.target.value });
  };
  getPhoneNumber = (event) => {
    // has to be change
    this.setState({ phoneNumber: event.target.value });
  };
  recoverPassword = () => {
    this.setState({ submit: true });
  };

  render() {
    if (this.state.phoneNumber !== "" && this.state.submit === true) {
      <div>{this.state.phoneNumber}</div>;
    }
    return (
      <section className="container recovery-position ">
        <ArrowBack />
        <div className="recovery-header">
          <h1 className="header-title">Password recovery</h1>
          <p className="header-subtitle">
            With this form you can create or recover a password if you are
            already a M&nbsp;Room customer.
          </p>
        </div>
        <div className="recovery-content">
          <div className="content-form">
            <Form>
              <div className="form-description">
                First, input your phone number and we'll send you a confirmation
                code via SMS.
              </div>
              <div className="form-field">
                <Form.Group controlId="formBasicPhone">
                  <Form.Label className="inputLable">Phone number</Form.Label>
                  <div className="drop">
                    <div className="dropDown">
                      <div className="countryCode">{this.state.value}</div>
                      <select
                        className="select"
                        name="code"
                        onChange={this.change}
                        value={this.state.value}
                      >
                        <option value="+372" defaultValue>
                          Estonia
                        </option>
                        <option value="+358">Finland</option>
                        <option value="+47">Norwar</option>
                        <option value="+45">Danmark</option>
                        <option value="+1">USA</option>
                        <option value="+46">Sweden</option>
                      </select>
                    </div>
                  </div>
                  <Form.Control
                    className="input phoneNumber"
                    type="number"
                    onChange={this.getPhoneNumber}
                    value={this.state.phoneNumber}
                    placeholder="401234567"
                  />
                </Form.Group>
              </div>
              <div className="form-submit">
                <Button
                  className="c-btn c-btn-small"
                  variant="primary"
                  type="submit"
                  onSubmit={this.recoverPassword}
                >
                  Send
                </Button>
              </div>
            </Form>
          </div>
        </div>
        <div className="recovery-spinner"></div>

        <div className="form-description">
          Next, input the confirmation code you received via SMS and your
          desired new password. The password must be at least 8 characters and
          include at least one uppercase letter, one lowercase letter and one
          number.
        </div>

        <Form.Group controlId="codeConfirmation">
          <Form.Label className="inputLable">Code confirmation</Form.Label>
          <Form.Control className="input" type="text" required />
        </Form.Group>
        <Form.Group controlId="newPassword">
          <Form.Label className="inputLable">Choose New Password</Form.Label>
          <Form.Control className="input" type="password" />
          <Form.Text className="text" id="passwordHelpBlock" muted>
            Password must be at least 8 characters and include at least one
            uppercase letter, one lowercase letter and one number.
          </Form.Text>
        </Form.Group>
      </section>
    );
  }
}
export default Recovery;
