import React, { Component } from "react";
import "./Recovery.css";

import { Form, Button } from "react-bootstrap";
import ArrowBack from "../../components/ArrowBack";
import Sendpass from "./component/SendPassword";
import GetCode from "./component/SendPassword";

class Recovery extends Component {
  state = {
    value: "+372",
    number: "",
    confirmCode: "",
    confirmPassword: "",
    showPhoneNumber: null,
    submit: true,
    receiveCode: false,
    recoverySuccessfull: false,
  };
  chooseCode = (event) => {
    this.setState({ value: event.target.value });
  };
  getPhoneNumber = (event) => {
    // has to be change
    this.setState({ number: event.target.value });
  };
  textSendTo = () => {
    this.setState({
      ...this.state,
      submit: false,
      receiveCode: true,
      fullphoneNumber: this.state.value + this.state.number,
    });
  };
  setPassword = (event) => {
    this.setState({
      ...this.state,
      submit: false,
      confirmPassword: event.target.value,
      receiveCode: false,
      recoverySuccessfull: true,
    });
  };

  render() {
    return (
      <section className="container recovery-position ">
        <ArrowBack />
        <div className="recovery-header">
          <h1 className="header-title">Password recovery</h1>
          <p className="header-subtitle">
            With this form you can create or recover a password if you are
            already a M&nbsp;Room customer.
          </p>
        </div>
        <div>
          {this.state.submit && (
            <Sendpass
              chooseCode={this.chooseCode}
              value={this.state.value}
              getPhoneNumber={this.getPhoneNumber}
              phoneNumber={this.state.number}
              sendText={this.textSendTo}
            >
              {this.state.value}
            </Sendpass>
          )}
          {this.state.receiveCode && (
            <div className="recovery-content">
              <div className="content-phoneNumber">
                {this.state.fullphoneNumber}
              </div>

              <Form>
                <Form.Text className="form-description">
                  Next, input the confirmation code you received via SMS and
                  your desired new password. The password must be at least 8
                  characters and include at least one uppercase letter, one
                  lowercase letter and one number.
                </Form.Text>
                <Form.Group controlId="codeConfirmation">
                  <Form.Label className="inputLable">
                    Code confirmation
                  </Form.Label>
                  <Form.Control
                    className="input"
                    type="text"
                    value={this.state.confirmCode}
                    required
                  />
                </Form.Group>
                <Form.Group controlId="newPassword">
                  <Form.Label className="inputLable">
                    Choose New Password
                  </Form.Label>
                  <Form.Control
                    className="input"
                    type="password"
                    value={this.state.confirmPassword}
                  />
                  <Form.Text className="text" id="passwordHelpBlock" muted>
                    Password must be at least 8 characters and include at least
                    one uppercase letter, one lowercase letter and one number.
                  </Form.Text>
                </Form.Group>
                <div className="form-submit">
                  <Button
                    className="c-btn c-btn-small"
                    variant="primary"
                    type="button"
                    onSubmit={this.setPassword}
                  >
                    Send
                  </Button>
                </div>
              </Form>
            </div>
          )}
          {this.state.recoverySuccessfull && <GetCode />}
        </div>
      </section>
    );
  }
}
export default Recovery;

